$(function() {

    // Changing Author Name <Email> to a mailto: link
    $('.author_link').each(function() {
        var author_split = $(this).html().split('&lt;'),
            author = author_split[0],
            email = author_split[1].replace('&gt;', ''),
            link = '<a href="mailto:' + email + '">' + author + '</a>';
            
        $(this).parent().append(link);
        $(this).remove();
    });

});